audiobitrate=$(jq -j ".configs[0] | .audiobitrate" $(pwd)/$(jq -j ".configs[0] | .selection" main-config.json))
resolution="$(jq -j ".configs[0] | .resolution" $(pwd)/$(jq -j ".configs[0] | .selection" main-config.json))"
crf="$(jq -j ".configs[0] | .quality" $(pwd)/$(jq -j ".configs[0] | .selection" main-config.json))"
ffmpeg -i "${1}" $audiobitrate -c:v libx264 $resolution  -preset "$(jq -r ".configs[0] | .preset" $(pwd)/$(jq -j ".configs[0] | .selection" main-config.json))" -crf "$crf" -fs "$(jq -j ".configs[0] | .size" $(pwd)/$(jq -j ".configs[0] | .selection" main-config.json))" "${1}"compressed.mp4